function quick_plot(dat, alpha, beta, lambda, ax, co)% function quick_plot(dat, alpha, beta, lambda, ax, co)%% plots data & fitif nargin < 4  returnendif nargin < 5 || isempty(ax)  ax = axes;endif nargin < 6 || isempty(co)  co = 'k';endaxes(ax);minx = min(dat(:, 1));maxx = max(dat(:, 1));minp = 1;% set coh=0 to minp, just for plottingdd = dat(:,1);dd(dd(:,1) == 0) = minp;% plot the dataplot(dd, dat(:,2), 'o', 'Color', co, ...    'MarkerSize', 4, 'markerFaceColor', co);hold on;% plot the fitxs = minx:0.1:maxx;plot(xs, quick_val(xs, alpha, beta, lambda), '-', 'Color', co, ...    'lineWidth', 1);% plot lapse lineplot([minx maxx], (1-lambda).*[1 1], 'k--');set(ax, 'XScale', 'log', 'XLim', [minp maxx], 'YLim', [0.5 1.0], ...    'XTick', dd, 'XTickLabel', num2str(dat(:,1)))