function [colors,colvals] = getColors% [colors,colvals] = getColors%	function to return a list of colors%   colors: the names of the colors, as a string cell array%   colvals: the shorthand string token used by plot() for the colors% RCS info: $Id$%% jpg - 6/2/98colarray = {...	'blue',		'b';...	'green',	'g';...	'red',		'r';...	'cyan',		'c';...	'magenta',	'm';...	'yellow',	'y';...	'black',	'k';...	'white',	'w'};colors = colarray(:,1);colvals = colarray(:,2);