function wysifig(fig, width, height)% function wysifig(fig, width, height)%% wysifig ... positions a figure on the top right--hand side of% the screen with the dimensions of a printed pageif nargin < 1    fig = figure;endif nargin < 2 || isempty(width)    width = 6.5;endif nargin < 3 || isempty(height)    height = 9;endset(fig, 'PaperUnits', 'inches', 'PaperPosition', [1 1 width height]);if ~strcmp(computer, 'PCWIN')    r = get(fig, 'Position');        r(1:2)=[5 145];    set(fig, 'Position', r);else    r = get(fig, 'Position');        r(1:2)=[5 50];    set(fig, 'Position', r);end    wysiwyg;